import request from '@/utils/request'

// 获取患者信息
export function getPatientInfo(patientId) {
    return request({
        url: `/patients/${patientId}`,
        method: 'get'
    })
}

// 获取评估信息
export function getAssessmentInfo(patientId) {
    return request({
        url: `/assessments/${patientId}`,
        method: 'get'
    })
}

// 保存评估信息
export function saveAssessment(data) {
    return request({
        url: '/assessments',
        method: 'post',
        data
    })
}

// 更新评估信息
export function updateAssessment(id, data) {
    return request({
        url: `/assessments/${id}`,
        method: 'put',
        data
    })
}
