import axios from 'axios'

const baseURL = 'http://localhost:8081/admission-assessment'

export const healthAssessmentApi = {
    // 创建健康评估
    createHealthAssessment(data) {
        return axios.post(`${baseURL}/health-assessment/create`, data)
    },

    // 获取健康评估详情
    getHealthAssessmentById(id) {
        return axios.get(`${baseURL}/health-assessment/${id}`)
    },

    // 更新健康评估
    updateHealthAssessment(id, data) {
        return axios.put(`${baseURL}/health-assessment/${id}`, data)
    },

    // 上传检查报告
    uploadMedicalReport(data) {
        return axios.post(`${baseURL}/health-assessment/upload-report`, data, {
            headers: {
                'Content-Type': 'multipart/form-data'
            }
        })
    },

    // 获取检查报告列表
    getMedicalReports(assessmentId) {
        return axios.get(`${baseURL}/health-assessment/${assessmentId}/reports`)
    },

    // 删除检查报告
    deleteMedicalReport(reportId) {
        return axios.delete(`${baseURL}/health-assessment/report/${reportId}`)
    },

    // 添加现病史
    addCurrentIllness(assessmentId, illnessName) {
        return axios.post(`${baseURL}/health-assessment/${assessmentId}/current-illness`, { illnessName })
    },

    // 添加既往病史
    addPastIllness(assessmentId, illnessName) {
        return axios.post(`${baseURL}/health-assessment/${assessmentId}/past-illness`, { illnessName })
    },

    // 删除病史
    deleteIllness(assessmentId, illnessId, type) {
        return axios.delete(`${baseURL}/health-assessment/${assessmentId}/illness/${type}/${illnessId}`)
    }
}
