import request from '@/utils/request'

// 获取入院评估列表
export function getAdmissionList(params) {
  return request({
    url: '/api/admission-assessment/list',
    method: 'get',
    params
  })
}

// 获取健康评估详情
export function getHealthAssessmentDetail(id) {
  return request({
    url: `/api/health-assessment/detail/${id}`,
    method: 'get'
  })
}

// 提交健康评估
export function submitHealthAssessment(data) {
  return request({
    url: '/api/health-assessment/submit',
    method: 'post',
    data
  })
}

// 获取照护评估详情
export function getCareAssessmentDetail(id) {
  return request({
    url: `/api/care-assessment/detail/${id}`,
    method: 'get'
  })
}

// 提交照护评估
export function submitCareAssessment(data) {
  return request({
    url: '/api/care-assessment/submit',
    method: 'post',
    data
  })
}

// 上传检查报告
export function uploadMedicalReport(assessmentId, reportType, file) {
    const formData = new FormData()
    formData.append('assessment_id', assessmentId)
    formData.append('report_type', reportType)
    formData.append('file', file)
    return request({
        url: '/api/medical-report/upload',
        method: 'post',
        data: formData,
        headers: {
            'Content-Type': 'multipart/form-data'
        }
    })
}
