import axios from 'axios'

const baseURL = 'http://localhost:8080/api'

// 获取入院评估列表
export function getAdmissionAssessmentList(params) {
    console.log('API Call - getAdmissionAssessmentList:', params);
    return axios.get(`${baseURL}/admission-assessment/list`, { params })
        .then(response => {
            console.log('API Response:', response);
            return response;
        })
        .catch(error => {
            console.error('API Error:', error);
            throw error;
        });
}

// 获取健康评估详情
export function getHealthAssessmentDetail(assessmentId) {
    return axios.get(`${baseURL}/health-assessment/detail`, { params: { assessmentId } })
}

// 提交健康评估
export function submitHealthAssessment(data) {
    return axios.post(`${baseURL}/health-assessment/submit`, data)
}

// 获取照护评估详情
export function getCareAssessmentDetail(assessmentId) {
    return axios.get(`${baseURL}/care-assessment/detail`, { params: { assessment_id: assessmentId } })
}

// 提交照护评估
export function submitCareAssessment(assessmentId, assessmentItems) {
    return axios.post(`${baseURL}/care-assessment/submit`, {
        assessment_id: assessmentId,
        assessment_items: assessmentItems
    })
}

// 上传检查报告
export function uploadMedicalReport(assessmentId, reportType, file) {
    const formData = new FormData()
    formData.append('assessment_id', assessmentId)
    formData.append('report_type', reportType)
    formData.append('file', file)
    return axios.post(`${baseURL}/medical-report/upload`, formData, {
        headers: {
            'Content-Type': 'multipart/form-data'
        }
    })
}
