import request from '@/utils/request'

// 获取入院评估列表
export function getAdmissionList(params) {
    return request({
        url: '/api/admission-assessment/list',
        method: 'get',
        params
    })
}

// 获取健康评估详情
export function getHealthAssessmentDetail(assessmentId) {
    return request({
        url: '/api/health-assessment/detail',
        method: 'get',
        params: { assessment_id: assessmentId }
    })
}

// 提交健康评估
export function submitHealthAssessment(assessmentId, data) {
    return request({
        url: '/api/health-assessment/submit',
        method: 'post',
        params: { assessment_id: assessmentId },
        data
    })
}

// 获取照护评估详情
export function getCareAssessmentDetail(assessmentId) {
    return request({
        url: '/api/care-assessment/detail',
        method: 'get',
        params: { assessment_id: assessmentId }
    })
}

// 提交照护评估
export function submitCareAssessment(assessmentId, data) {
    return request({
        url: '/api/care-assessment/submit',
        method: 'post',
        params: { assessment_id: assessmentId },
        data
    })
}

// 上传检查报告
export function uploadMedicalReport(assessmentId, reportType, file) {
    const formData = new FormData()
    formData.append('file', file)
    return request({
        url: '/api/medical-report/upload',
        method: 'post',
        params: {
            assessment_id: assessmentId,
            report_type: reportType
        },
        data: formData,
        headers: {
            'Content-Type': 'multipart/form-data'
        }
    })
}
