import axios from 'axios'

const baseURL = 'http://localhost:8081/admission-assessment'

export const admissionApi = {
    // 获取入院评估列表
    getAdmissionList(params) {
        return axios.get(`${baseURL}/list`, { params })
    },

    // 创建入院评估
    createAssessment(data) {
        return axios.post(`${baseURL}/create`, data)
    },

    // 获取评估详情
    getAssessmentById(assessmentId) {
        return axios.get(`${baseURL}/${assessmentId}`)
    },

    // 获取患者的所有评估
    getAssessmentsByPatientId(patientId) {
        return axios.get(`${baseURL}/patient/${patientId}`)
    },

    // 获取健康评估列表
    getHealthAssessments(assessmentId) {
        return axios.get(`${baseURL}/${assessmentId}/health-assessments`)
    },

    // 更新评估状态
    updateAssessmentStatus(assessmentId, status) {
        return axios.put(`${baseURL}/${assessmentId}/status`, { status })
    },

    // 获取待评估数量
    getPendingAssessmentCount() {
        return axios.get(`${baseURL}/pending-count`)
    }
}
