import Vue from 'vue'
import Vuex from 'vuex'
import { getAdmissionAssessmentList, getHealthAssessmentDetail, submitHealthAssessment, getCareAssessmentDetail, submitCareAssessment, uploadMedicalReport } from '../api/admission'

Vue.use(Vuex)

export default new Vuex.Store({
  state: {
    patients: []
  },
  getters: {
    pendingAdmissionCount(state) {
      return state.patients.filter(patient =>
        patient.healthStatus === '待评估' ||
        patient.careStatus === '待评估'
      ).length;
    },
    pendingDischargeCount(state) {
      return state.patients.filter(p => p.status === '待出院').length;
    }
  },
  mutations: {
    setPatients(state, patients) {
      state.patients = patients;
    },
    updatePatientHealthAssessment(state, { patientId, status, time, operator }) {
      const patient = state.patients.find(p => p.patientId === patientId);
      if (patient) {
        patient.healthStatus = status;
        patient.healthTime = time;
        patient.healthOperator = operator;
      }
    },
    updatePatientCareAssessment(state, { patientId, status, time, operator }) {
      const patient = state.patients.find(p => p.patientId === patientId);
      if (patient) {
        patient.careStatus = status;
        patient.careTime = time;
        patient.careOperator = operator;
      }
    },
    completeAdmission(state, patientId) {
      const patient = state.patients.find(p => p.id === patientId);
      if (patient) {
        patient.admissionCompleted = true;
      }
    }
  },
  actions: {
    async fetchPatients({ commit }, params) {
      try {
        console.log('Fetching patients with params:', params);
        const response = await getAdmissionAssessmentList(params);
        console.log('API Response:', response);
        if (response.data && response.data.data) {
          console.log('Setting patients:', response.data.data.list);
          commit('setPatients', response.data.data.list);
        } else {
          console.warn('Invalid response format:', response);
        }
      } catch (error) {
        console.error('获取患者列表失败:', error);
      }
    },
    async updateHealthAssessment({ commit }, payload) {
      try {
        await submitHealthAssessment(payload);
        commit('updatePatientHealthAssessment', {
          patientId: payload.patientId,
          status: payload.status === 'completed' ? '完成' :
            payload.status === 'rejected' ? '不同意入院' : '待评估',
          time: payload.time,
          operator: payload.operator
        });
      } catch (error) {
        console.error('更新健康评估失败:', error);
        throw error;
      }
    },
    async updateCareAssessment({ commit }, payload) {
      try {
        await submitCareAssessment(payload);
        commit('updatePatientCareAssessment', {
          patientId: payload.patientId,
          status: payload.status === 'completed' ? '完成' :
            payload.status === 'rejected' ? '不同意入院' : '待评估',
          time: payload.time,
          operator: payload.operator
        });
      } catch (error) {
        console.error('更新照护评估失败:', error);
        throw error;
      }
    },
    async fetchHealthAssessmentDetail(commit, id) {
      const response = await getHealthAssessmentDetail(id);
      return response;
    },
    async fetchCareAssessmentDetail(commit, id) {
      const response = await getCareAssessmentDetail(id);
      return response;
    },
    async uploadMedicalReport(commit, payload) {
      const response = await uploadMedicalReport(payload);
      return response;
    },
    async finishAdmission({ commit }, patientId) {
      try {
        // 这里可以添加完成入院的 API 调用
        commit('completeAdmission', patientId);
      } catch (error) {
        console.error('完成入院失败:', error);
        throw error;
      }
    }
  },
  modules: {
  }
})
