import axios from 'axios'
import { Message } from 'element-ui'

// 创建axios实例
const service = axios.create({
    baseURL: 'http://localhost:8080/api',  // 添加/api前缀到baseURL
    timeout: 5000,
    headers: {
        'Content-Type': 'application/json;charset=UTF-8'
    },
    withCredentials: true
})

// 请求拦截器
service.interceptors.request.use(
    config => {
        // 在发送请求之前做些什么
        console.log('发送请求:', config.url)  // 添加请求URL日志
        return config
    },
    error => {
        // 对请求错误做些什么
        console.error('请求发送失败:', error)
        return Promise.reject(error)
    }
)

// 响应拦截器
service.interceptors.response.use(
    response => {
        const res = response.data
        // 如果返回的状态码不是200，说明接口有问题，把错误信息显示出来
        if (res.code !== 200) {
            Message({
                message: res.message || '请求失败',
                type: 'error',
                duration: 5 * 1000
            })
            return Promise.reject(new Error(res.message || '请求失败'))
        } else {
            return res
        }
    },
    error => {
        console.error('请求失败:', error.config?.url, error)  // 添加失败的URL信息
        // 处理404错误
        if (error.response && error.response.status === 404) {
            Message({
                message: `请求的资源不存在: ${error.config?.url}`,  // 添加URL信息到错误消息
                type: 'error',
                duration: 5 * 1000
            })
        } else {
            Message({
                message: error.message || '请求失败，请稍后重试',
                type: 'error',
                duration: 5 * 1000
            })
        }
        return Promise.reject(error)
    }
)

export default service
