import request from '@/utils/request'

/**
 * 获取患者详情
 * @param {number} patientId - 患者ID
 * @returns {Promise} - 返回患者详情数据
 */
export function getPatientDetail(patientId) {
    return request({
        url: '/patient-info/detail',
        method: 'get',
        params: {
            id: patientId
        }
    })
}

/**
 * 获取患者列表
 * @param {Object} params - 查询参数
 * @returns {Promise} - 返回患者列表数据
 */
export function getPatientList(params) {
    return request({
        url: '/patient-info/list',
        method: 'get',
        params
    })
}

/**
 * 创建患者信息
 * @param {Object} data - 患者信息数据
 * @returns {Promise} - 返回创建结果
 */
export function createPatient(data) {
    return request({
        url: '/api/patient-info/create',
        method: 'post',
        data
    })
}

/**
 * 更新患者信息
 * @param {Object} data - 患者信息数据
 * @returns {Promise} - 返回更新结果
 */
export function updatePatient(data) {
    return request({
        url: '/api/patient-info/update',
        method: 'put',
        data
    })
}
