import "core-js/modules/es.array.push.js";
import "core-js/modules/es.iterator.constructor.js";
import "core-js/modules/es.iterator.filter.js";
import "core-js/modules/es.iterator.map.js";
import "core-js/modules/es.iterator.some.js";
import { mapState } from 'vuex';
import SymptomDialog from '../components/SymptomDialog.vue';
import { submitHealthAssessment } from '@/api/healthAssessment';
export default {
  name: 'HealthAssessment',
  components: {
    SymptomDialog
  },
  data() {
    return {
      id: this.$route.params.id,
      activeTab: 'health',
      patient: {
        name: '',
        gender: '',
        age: '',
        idNumber: '',
        registrationTime: ''
      },
      dialogVisible: false,
      dialogType: '',
      dialogMode: '',
      dialogInitialSelected: [],
      currentIllnesses: [],
      pastIllnesses: [],
      diet: {
        noSpicy: false,
        liquidDiet: false,
        semiLiquidDiet: false,
        softDiet: false,
        lowSalt: false,
        lowSugar: false,
        diabeticDiet: false,
        renalDiet: false,
        highSalt: false,
        highSugar: false,
        highFat: false,
        seafood: false
      },
      uploadFiles: {
        biochemical: [],
        xray: [],
        ultrasound: [],
        ecg: [],
        ct: [],
        other: []
      },
      medicalForm: {
        forbiddenMedicine: '',
        conclusion: '',
        admissionDecision: 'admit'
      },
      currentSearchKeyword: '',
      showCurrentSuggestions: false,
      currentFilteredSymptoms: [],
      pastSearchKeyword: '',
      showPastSuggestions: false,
      pastFilteredSymptoms: [],
      allSymptoms: [],
      // 所有可选症状的列表
      careForm: {
        selfCare: {
          daily: 0,
          eating: 0,
          toilet: 0,
          notes: ''
        },
        cognition: {
          environment: 0,
          time: 0,
          people: 0,
          notes: ''
        },
        emotion: {
          anxiety: 0,
          depression: 0,
          behavior: 0,
          notes: ''
        },
        extreme: {
          selfHarm: 0,
          aggression: 0,
          notes: ''
        }
      },
      assessmentId: null,
      formData: {
        // 添加表单数据字段
        currentIllnesses: [],
        pastIllnesses: [],
        forbiddenMedicine: '',
        biochemicalReports: [],
        xrayReports: [],
        ultrasoundReports: []
        // ... 其他表单字段
      }
    };
  },
  computed: {
    ...mapState({
      patients: state => state.patients
    })
  },
  created() {
    // 从路由参数中获取患者ID
    const patientId = this.$route.query.patientId;
    if (!patientId) {
      this.$message.error('缺少患者ID参数');
      this.goBack();
      return;
    }

    // 加载健康评估详情
    this.loadAssessmentDetail(patientId);
    // 加载所有可选症状
    this.loadAllSymptoms();
  },
  methods: {
    formatDateTime(dateTimeStr) {
      if (!dateTimeStr) return '';
      const date = new Date(dateTimeStr);
      return `${date.getFullYear()}-${String(date.getMonth() + 1).padStart(2, '0')}-${String(date.getDate()).padStart(2, '0')} ${String(date.getHours()).padStart(2, '0')}:${String(date.getMinutes()).padStart(2, '0')}`;
    },
    async loadAssessmentDetail(patientId) {
      try {
        const response = await this.$http.get('/api/health-assessment/detail', {
          params: {
            assessment_id: patientId
          }
        });
        if (response.data.code === 200) {
          // 更新表单数据
          const data = response.data.data;
          this.patient = {
            id: data.patientId,
            name: data.patientName,
            gender: data.patientGender,
            age: data.patientAge,
            idNumber: data.patientIdNumber,
            registrationTime: data.registrationTime
          };

          // 更新评估数据
          this.currentIllnesses = data.currentIllnesses ? data.currentIllnesses.split(',').map(name => ({
            id: Date.now(),
            name
          })) : [];
          this.pastIllnesses = data.pastIllnesses ? data.pastIllnesses.split(',').map(name => ({
            id: Date.now(),
            name
          })) : [];
          this.medicalForm.forbiddenMedicine = data.forbiddenMedicine || '';

          // 更新上传文件列表
          this.uploadFiles = {
            biochemical: data.biochemicalReports ? data.biochemicalReports.split(',').map(name => ({
              name
            })) : [],
            xray: data.xrayReports ? data.xrayReports.split(',').map(name => ({
              name
            })) : [],
            ultrasound: data.ultrasoundReports ? data.ultrasoundReports.split(',').map(name => ({
              name
            })) : []
          };
        } else {
          this.$message.error(response.data.message || '加载评估详情失败');
        }
      } catch (error) {
        console.error('加载评估详情失败:', error);
        this.$message.error('加载评估详情失败，请稍后重试');
      }
    },
    // 检查表单是否完整填写
    checkFormComplete() {
      // 根据实际表单字段检查是否都已填写
      const requiredFields = ['currentIllnesses', 'pastIllnesses', 'forbiddenMedicine'
      // ... 其他必填字段
      ];
      return requiredFields.every(field => {
        if (Array.isArray(this.formData[field])) {
          return this.formData[field].length > 0;
        }
        return !!this.formData[field];
      });
    },
    async handleSave() {
      try {
        // 准备提交的数据
        const submitData = {
          ...this.formData,
          assessmentId: this.assessmentId,
          status: this.checkFormComplete() ? '完成' : '未完成'
        };
        const response = await submitHealthAssessment(submitData);
        if (response.code === 200) {
          this.$message.success('保存成功');
          if (submitData.status === '完成') {
            this.$message.success('评估已完成');
          }
          // 返回列表页面
          this.goBack();
        } else {
          this.$message.error(response.message || '保存失败');
        }
      } catch (error) {
        console.error('保存评估失败:', error);
        this.$message.error('保存失败，请稍后重试');
      }
    },
    goBack() {
      this.$router.push('/admission-assessment/list');
    },
    // 加载所有可选症状
    async loadAllSymptoms() {
      try {
        // 从后端API获取疾病字典
        const response = await this.$http.get('/api/disease-dict/list');
        if (response.data.code === 200) {
          this.allSymptoms = response.data.data;
        } else {
          console.error('加载疾病字典失败:', response.data.message);
          // 使用默认数据作为备选
          this.useDefaultSymptoms();
        }
      } catch (error) {
        console.error('获取疾病字典出错:', error);
        // 发生错误时使用默认数据
        this.useDefaultSymptoms();
      }
    },
    // 使用默认的症状数据
    useDefaultSymptoms() {
      // 常见疾病分类
      const symptomsCategories = {
        '心脏类': [{
          id: 1,
          name: '高血压'
        }, {
          id: 2,
          name: '心脏病'
        }, {
          id: 3,
          name: '心绞痛'
        }, {
          id: 4,
          name: '冠心病'
        }, {
          id: 5,
          name: '心律不齐'
        }],
        '神经类': [{
          id: 6,
          name: '头痛'
        }, {
          id: 7,
          name: '偏头痛'
        }, {
          id: 8,
          name: '癫痫'
        }, {
          id: 9,
          name: '帕金森'
        }, {
          id: 10,
          name: '失眠'
        }],
        '消化系统': [{
          id: 11,
          name: '胃病'
        }, {
          id: 12,
          name: '胃炎'
        }, {
          id: 13,
          name: '胃溃疡'
        }, {
          id: 14,
          name: '肠炎'
        }, {
          id: 15,
          name: '十二指肠溃疡'
        }],
        '呼吸系统': [{
          id: 16,
          name: '哮喘'
        }, {
          id: 17,
          name: '支气管炎'
        }, {
          id: 18,
          name: '肺炎'
        }, {
          id: 19,
          name: '肺结核'
        }, {
          id: 20,
          name: '肺气肿'
        }],
        '代谢类': [{
          id: 21,
          name: '糖尿病'
        }, {
          id: 22,
          name: '高血脂'
        }, {
          id: 23,
          name: '痛风'
        }, {
          id: 24,
          name: '肥胖'
        }, {
          id: 25,
          name: '甲状腺功能亢进'
        }]
      };

      // 合并所有分类的疾病到一个数组
      this.allSymptoms = Object.values(symptomsCategories).flat();
    },
    // 搜索症状
    searchSymptoms(type) {
      const keyword = type === 'current' ? this.currentSearchKeyword : this.pastSearchKeyword;
      if (!keyword) {
        if (type === 'current') {
          this.currentFilteredSymptoms = [];
        } else {
          this.pastFilteredSymptoms = [];
        }
        return;
      }

      // 过滤已经添加的症状
      const existingIds = type === 'current' ? this.currentIllnesses.map(s => s.id) : this.pastIllnesses.map(s => s.id);

      // 过滤匹配关键词且未添加的症状
      const filtered = this.allSymptoms.filter(symptom => symptom.name.toLowerCase().includes(keyword.toLowerCase()) && !existingIds.includes(symptom.id));
      if (type === 'current') {
        this.currentFilteredSymptoms = filtered;
      } else {
        this.pastFilteredSymptoms = filtered;
      }
    },
    // 添加症状
    addSymptom(type, symptom) {
      if (type === 'current') {
        if (!this.currentIllnesses.some(s => s.id === symptom.id)) {
          this.currentIllnesses.push(symptom);
          this.currentSearchKeyword = '';
          this.currentFilteredSymptoms = [];
        }
      } else if (type === 'past') {
        if (!this.pastIllnesses.some(s => s.id === symptom.id)) {
          this.pastIllnesses.push(symptom);
          this.pastSearchKeyword = '';
          this.pastFilteredSymptoms = [];
        }
      }
    },
    // 移除症状
    removeSymptom(type, id) {
      if (type === 'current') {
        this.currentIllnesses = this.currentIllnesses.filter(s => s.id !== id);
      } else if (type === 'past') {
        this.pastIllnesses = this.pastIllnesses.filter(s => s.id !== id);
      }
    },
    // 延迟隐藏建议，以便能点击建议项
    hideSuggestionsDelayed(type) {
      setTimeout(() => {
        if (type === 'current') {
          this.showCurrentSuggestions = false;
        } else {
          this.showPastSuggestions = false;
        }
      }, 200);
    },
    // 处理文件上传
    async handleFileUpload(event, type) {
      const file = event.target.files[0];
      if (!file) return;

      // 验证文件
      const isJPGOrPNG = file.type === 'image/jpeg' || file.type === 'image/png';
      const isLt5M = file.size / 1024 / 1024 < 5;
      if (!isJPGOrPNG) {
        // 显示错误提示
        return;
      }
      if (!isLt5M) {
        // 显示错误提示
        return;
      }
      try {
        const formData = new FormData();
        formData.append('file', file);
        formData.append('assessment_id', this.id);
        formData.append('report_type', type);
        const response = await this.$http.post('/api/medical-report/upload', formData, {
          headers: {
            'Content-Type': 'multipart/form-data'
          }
        });
        if (response.data.code === 200) {
          // 添加到上传文件列表
          if (!this.uploadFiles[type]) {
            this.uploadFiles[type] = [];
          }
          this.uploadFiles[type].push(response.data.data);
        } else {
          console.error('上传文件失败:', response.data.message);
        }
      } catch (error) {
        console.error('上传文件失败:', error);
      }
    },
    // 移除已上传的文件
    removeFile(type, index) {
      if (this.uploadFiles[type] && this.uploadFiles[type].length > index) {
        this.uploadFiles[type].splice(index, 1);
      }
    },
    showSymptomDialog(type) {
      this.dialogVisible = true;
      this.dialogMode = type;
      if (type === 'current') {
        this.dialogType = '现病史';
        this.dialogInitialSelected = this.currentIllnesses.map(s => s.id);
      } else if (type === 'past') {
        this.dialogType = '既往病史';
        this.dialogInitialSelected = this.pastIllnesses.map(s => s.id);
      }
    },
    confirmSymptoms(selectedSymptoms) {
      if (this.dialogMode === 'current') {
        this.currentIllnesses = selectedSymptoms;
      } else if (this.dialogMode === 'past') {
        this.pastIllnesses = selectedSymptoms;
      }
      this.dialogVisible = false;
    },
    cancelDialog() {
      this.dialogVisible = false;
    },
    setCareLevel(category, item, level) {
      this.careForm[category][item] = level;
    },
    getProgressWidth(category, item) {
      const level = this.careForm[category][item];
      return `${(level + 1) * 25}%`;
    },
    async saveCareAssessment() {
      try {
        const now = new Date();
        const timeStr = `${now.getFullYear()}-${String(now.getMonth() + 1).padStart(2, '0')}-${String(now.getDate()).padStart(2, '0')} ${String(now.getHours()).padStart(2, '0')}:${String(now.getMinutes()).padStart(2, '0')}`;

        // 准备要保存的照护评估详情数据
        const details = {
          selfCare: {
            ...this.careForm.selfCare
          },
          cognition: {
            ...this.careForm.cognition
          },
          emotion: {
            ...this.careForm.emotion
          },
          extreme: {
            ...this.careForm.extreme
          }
        };

        // 更新照护评估
        await this.$store.dispatch('updateCareAssessment', {
          patientId: parseInt(this.id),
          status: 'completed',
          time: timeStr,
          operator: '李护士',
          // 添加经办人信息
          details: details
        });

        // 如果健康评估和照护评估都已完成，标记入院流程完成
        if (this.patient.healthAssessment.status === 'completed' && (this.patient.careAssessment.status === 'completed' || details)) {
          await this.$store.dispatch('finishAdmission', parseInt(this.id));
        }

        // 返回评估列表页面
        this.$router.push('/admission-assessment/list');
      } catch (error) {
        console.error('保存照护评估失败:', error);
        // 这里可以添加错误提示
      }
    },
    async handleMedicalReportUpload(file) {
      try {
        const formData = new FormData();
        formData.append('file', file);
        formData.append('patientId', this.id);
        await this.$store.dispatch('uploadMedicalReport', formData);
        this.$message.success('医疗报告上传成功');
      } catch (error) {
        console.error('上传医疗报告失败:', error);
        this.$message.error('上传医疗报告失败');
      }
    },
    async saveAssessment() {
      try {
        // 准备提交的数据
        const assessmentData = {
          patientId: this.$route.query.patientId,
          // 从路由参数获取患者ID
          currentIllnesses: this.currentIllnesses.map(s => s.name).join(','),
          pastIllnesses: this.pastIllnesses.map(s => s.name).join(','),
          forbiddenMedicine: this.medicalForm.forbiddenMedicine.map(s => s.name).join(','),
          biochemicalReports: this.uploadFiles.biochemical?.map(f => f.name).join(',') || '',
          xrayReports: this.uploadFiles.xray?.map(f => f.name).join(',') || '',
          ultrasoundReports: this.uploadFiles.ultrasound?.map(f => f.name).join(',') || '',
          status: this.checkFormComplete() ? '完成' : '未完成'
        };

        // 调用保存接口
        const response = await this.$http.post('/api/health-assessment/submit', assessmentData);
        if (response.data.code === 200) {
          this.$message.success('保存成功');
          if (assessmentData.status === '完成') {
            this.$message.success('评估已完成');
          }
          // 返回列表页面
          this.goBack();
        } else {
          this.$message.error(response.data.message || '保存失败');
        }
      } catch (error) {
        console.error('保存评估失败:', error);
        this.$message.error('保存失败：' + (error.response?.data?.message || error.message));
      }
    }
  }
};
