import request from '@/utils/request'

/**
 * 获取患者详情
 * @param {number} patient_id - 患者ID
 * @returns {Promise} - 返回患者详情数据
 */
export function getPatientDetail(patient_id) {
    return request({
        url: '/patientInfo/detail',
        method: 'get',
        params: {
            patient_id
        }
    })
}

/**
 * 获取患者列表
 * @param {Object} params - 查询参数
 * @returns {Promise} - 返回患者列表数据
 */
export function getPatientList(params) {
    return request({
        url: '/patientInfo/list',
        method: 'get',
        params
    })
}

/**
 * 创建患者信息
 * @param {Object} data - 患者信息数据
 * @returns {Promise} - 返回创建结果
 */
export function createPatient(data) {
    return request({
        url: '/patientInfo/create',
        method: 'post',
        data
    })
}

/**
 * 更新患者信息
 * @param {Object} data - 患者信息数据
 * @returns {Promise} - 返回更新结果
 */
export function updatePatient(data) {
    return request({
        url: '/patientInfo/update',
        method: 'put',
        data
    })
}
