import request from '@/utils/request'

// 获取健康评估详情
export function getHealthAssessment(patientId) {
    return request({
        url: `/api/health-assessment/${patientId}`,
        method: 'get'
    })
}

// 获取健康评估详情（别名方法）
export function getHealthAssessmentDetail(id) {
    return getHealthAssessment(id)
}

// 创建健康评估
export function createHealthAssessment(data) {
    return request({
        url: '/api/health-assessment',
        method: 'post',
        data
    })
}

// 提交健康评估
export function submitHealthAssessment(data) {
    return request({
        url: '/api/health-assessment/submit',
        method: 'post',
        data
    })
}

// 更新健康评估
export function updateHealthAssessment(id, data) {
    return request({
        url: `/api/health-assessment/${id}`,
        method: 'put',
        data
    })
}

// 获取健康评估列表
export function getHealthAssessmentList(params) {
    return request({
        url: '/api/health-assessment',
        method: 'get',
        params
    })
}
