          this.$set(targetList, index, data);
          this.$message.success('医嘱更新成功');
        } else {
          this.$message.error('未找到要更新的医嘱');
        }
      } else {
        targetList.unshift(data); // 添加到列表开头
        this.$message.success('新医嘱添加成功');
