import request from '@/utils/request'

// 获取入院评估列表
export function getAdmissionList(params) {
    return request({
        url: '/api/admission-assessment',
        method: 'get',
        params
    })
}

// 获取患者信息
export function getPatientInfo(patientId) {
    return request({
        url: `/api/patient-info/${patientId}`,
        method: 'get'
    })
}

// 创建入院评估
export function createAdmissionAssessment(data) {
    return request({
        url: '/api/admission-assessment',
        method: 'post',
        data
    })
}

// 更新入院评估
export function updateAdmissionAssessment(id, data) {
    return request({
        url: `/api/admission-assessment/${id}`,
        method: 'put',
        data
    })
}

// 提交健康评估（别名方法）
export function submitHealthAssessment(data) {
    return request({
        url: '/api/health-assessment',
        method: 'post',
        data
    })
}

// 提交照护评估
export function submitCareAssessment(data) {
    return request({
        url: '/api/care-assessment',
        method: 'post',
        data
    })
}

// 获取健康评估详情
export function getHealthAssessmentDetail(id) {
    return request({
        url: `/api/health-assessment/${id}`,
        method: 'get'
    })
}

// 获取照护评估详情
export function getCareAssessmentDetail(id) {
    return request({
        url: `/api/care-assessment/${id}`,
        method: 'get'
    })
}

// 上传医疗报告
export function uploadMedicalReport(data) {
    return request({
        url: '/api/medical-report/upload',
        method: 'post',
        data,
        headers: {
            'Content-Type': 'multipart/form-data'
        }
    })
}
