import "core-js/modules/es.array.push.js";
import "core-js/modules/es.iterator.constructor.js";
import "core-js/modules/es.iterator.filter.js";
import "core-js/modules/es.iterator.find.js";
import "core-js/modules/es.iterator.for-each.js";
import "core-js/modules/es.iterator.map.js";
import "core-js/modules/es.iterator.some.js";
import { mapState } from 'vuex';
import SymptomDialog from '../components/SymptomDialog.vue';
export default {
  name: 'HealthAssessment',
  components: {
    SymptomDialog
  },
  props: {
    id: {
      type: [Number, String],
      required: true
    }
  },
  data() {
    return {
      activeTab: 'health',
      dialogVisible: false,
      dialogType: '',
      dialogMode: '',
      dialogInitialSelected: [],
      currentIllnesses: [],
      pastIllnesses: [],
      diet: {
        noSpicy: false,
        liquidDiet: false,
        semiLiquidDiet: false,
        softDiet: false,
        lowSalt: false,
        lowSugar: false,
        diabeticDiet: false,
        renalDiet: false,
        highSalt: false,
        highSugar: false,
        highFat: false,
        seafood: false
      },
      uploadFiles: {
        biochemical: [],
        xray: [],
        ultrasound: [],
        ecg: [],
        ct: [],
        other: []
      },
      medicalForm: {
        forbiddenMedicine: '',
        conclusion: '',
        admissionDecision: 'admit'
      },
      currentSearchKeyword: '',
      showCurrentSuggestions: false,
      currentFilteredSymptoms: [],
      pastSearchKeyword: '',
      showPastSuggestions: false,
      pastFilteredSymptoms: [],
      allSymptoms: [],
      // 所有可选症状的列表
      careForm: {
        selfCare: {
          daily: 0,
          eating: 0,
          toilet: 0,
          notes: ''
        },
        cognition: {
          environment: 0,
          time: 0,
          people: 0,
          notes: ''
        },
        emotion: {
          anxiety: 0,
          depression: 0,
          behavior: 0,
          notes: ''
        },
        extreme: {
          selfHarm: 0,
          aggression: 0,
          notes: ''
        }
      }
    };
  },
  computed: {
    ...mapState({
      patients: state => state.patients
    }),
    patient() {
      // 从Vuex状态中获取患者数据
      const patientId = parseInt(this.id);
      return this.patients.find(p => p.id === patientId) || {
        name: '',
        gender: '',
        age: '',
        idNumber: '',
        registrationTime: ''
      };
    }
  },
  methods: {
    async loadAssessmentDetails() {
      try {
        // 直接调用API而不是通过Vuex（可以根据实际情况选择）
        const response = await this.$axios.get(`/api/health-assessment/detail?assessmentId=${this.id}`);
        if (response.data.code === 200) {
          const details = response.data.data;
          if (details.currentIllness) {
            this.currentIllnesses = details.currentIllness.map(item => ({
              id: item.id,
              name: item.name
            }));
          }
          if (details.dietRestrictions) {
            this.diet = {
              noSpicy: false,
              liquidDiet: false,
              semiLiquidDiet: false,
              softDiet: false,
              lowSalt: details.dietRestrictions.some(item => item.name === '低盐饮食'),
              lowSugar: details.dietRestrictions.some(item => item.name === '低糖饮食'),
              diabeticDiet: false,
              renalDiet: false,
              highSalt: false,
              highSugar: false,
              highFat: false,
              seafood: false
            };
          }
          if (details.forbiddenMedicines) {
            this.medicalForm.forbiddenMedicine = details.forbiddenMedicines;
          }
          if (details.physicalConclusion) {
            this.medicalForm.conclusion = details.physicalConclusion;
          }
          if (details.admissionAgreement === '同意入院') {
            this.medicalForm.admissionDecision = 'admit';
          } else {
            this.medicalForm.admissionDecision = 'reject';
          }
        } else {
          console.error('加载评估详情失败:', response.data.message);
        }
      } catch (error) {
        console.error('加载评估详情失败:', error);
      }
    },
    // 加载所有可选症状
    async loadAllSymptoms() {
      try {
        // 从后端API获取疾病字典
        const response = await this.$axios.get('/api/disease-dict/list');
        if (response.data.code === 200) {
          this.allSymptoms = response.data.data;
        } else {
          console.error('加载疾病字典失败:', response.data.message);
          // 使用默认数据作为备选
          this.useDefaultSymptoms();
        }
      } catch (error) {
        console.error('获取疾病字典出错:', error);
        // 发生错误时使用默认数据
        this.useDefaultSymptoms();
      }
    },
    // 使用默认的症状数据
    useDefaultSymptoms() {
      // 常见疾病分类
      const symptomsCategories = {
        '心脏类': [{
          id: 1,
          name: '高血压'
        }, {
          id: 2,
          name: '心脏病'
        }, {
          id: 3,
          name: '心绞痛'
        }, {
          id: 4,
          name: '冠心病'
        }, {
          id: 5,
          name: '心律不齐'
        }],
        '神经类': [{
          id: 6,
          name: '头痛'
        }, {
          id: 7,
          name: '偏头痛'
        }, {
          id: 8,
          name: '癫痫'
        }, {
          id: 9,
          name: '帕金森'
        }, {
          id: 10,
          name: '失眠'
        }],
        '消化系统': [{
          id: 11,
          name: '胃病'
        }, {
          id: 12,
          name: '胃炎'
        }, {
          id: 13,
          name: '胃溃疡'
        }, {
          id: 14,
          name: '肠炎'
        }, {
          id: 15,
          name: '十二指肠溃疡'
        }],
        '呼吸系统': [{
          id: 16,
          name: '哮喘'
        }, {
          id: 17,
          name: '支气管炎'
        }, {
          id: 18,
          name: '肺炎'
        }, {
          id: 19,
          name: '肺结核'
        }, {
          id: 20,
          name: '肺气肿'
        }],
        '代谢类': [{
          id: 21,
          name: '糖尿病'
        }, {
          id: 22,
          name: '高血脂'
        }, {
          id: 23,
          name: '痛风'
        }, {
          id: 24,
          name: '肥胖'
        }, {
          id: 25,
          name: '甲状腺功能亢进'
        }]
      };

      // 合并所有分类的疾病到一个数组
      this.allSymptoms = Object.values(symptomsCategories).flat();
    },
    // 搜索症状
    searchSymptoms(type) {
      const keyword = type === 'current' ? this.currentSearchKeyword : this.pastSearchKeyword;
      if (!keyword) {
        if (type === 'current') {
          this.currentFilteredSymptoms = [];
        } else {
          this.pastFilteredSymptoms = [];
        }
        return;
      }

      // 过滤已经添加的症状
      const existingIds = type === 'current' ? this.currentIllnesses.map(s => s.id) : this.pastIllnesses.map(s => s.id);

      // 过滤匹配关键词且未添加的症状
      const filtered = this.allSymptoms.filter(symptom => symptom.name.toLowerCase().includes(keyword.toLowerCase()) && !existingIds.includes(symptom.id));
      if (type === 'current') {
        this.currentFilteredSymptoms = filtered;
      } else {
        this.pastFilteredSymptoms = filtered;
      }
    },
    // 添加症状
    addSymptom(type, symptom) {
      if (type === 'current') {
        if (!this.currentIllnesses.some(s => s.id === symptom.id)) {
          this.currentIllnesses.push(symptom);
          this.currentSearchKeyword = '';
          this.currentFilteredSymptoms = [];
        }
      } else if (type === 'past') {
        if (!this.pastIllnesses.some(s => s.id === symptom.id)) {
          this.pastIllnesses.push(symptom);
          this.pastSearchKeyword = '';
          this.pastFilteredSymptoms = [];
        }
      }
    },
    // 移除症状
    removeSymptom(type, id) {
      if (type === 'current') {
        this.currentIllnesses = this.currentIllnesses.filter(s => s.id !== id);
      } else if (type === 'past') {
        this.pastIllnesses = this.pastIllnesses.filter(s => s.id !== id);
      }
    },
    // 延迟隐藏建议，以便能点击建议项
    hideSuggestionsDelayed(type) {
      setTimeout(() => {
        if (type === 'current') {
          this.showCurrentSuggestions = false;
        } else {
          this.showPastSuggestions = false;
        }
      }, 200);
    },
    // 处理文件上传
    async handleFileUpload(event, type) {
      const file = event.target.files[0];
      if (!file) return;

      // 验证文件
      const isJPGOrPNG = file.type === 'image/jpeg' || file.type === 'image/png';
      const isLt5M = file.size / 1024 / 1024 < 5;
      if (!isJPGOrPNG) {
        // 显示错误提示
        return;
      }
      if (!isLt5M) {
        // 显示错误提示
        return;
      }
      try {
        const formData = new FormData();
        formData.append('file', file);
        formData.append('assessment_id', this.id);
        formData.append('report_type', type);
        const response = await this.$axios.post('/api/medical-report/upload', formData, {
          headers: {
            'Content-Type': 'multipart/form-data'
          }
        });
        if (response.data.code === 200) {
          // 添加到上传文件列表
          if (!this.uploadFiles[type]) {
            this.uploadFiles[type] = [];
          }
          this.uploadFiles[type].push(response.data.data);
        } else {
          console.error('上传文件失败:', response.data.message);
        }
      } catch (error) {
        console.error('上传文件失败:', error);
      }
    },
    // 移除已上传的文件
    removeFile(type, index) {
      if (this.uploadFiles[type] && this.uploadFiles[type].length > index) {
        this.uploadFiles[type].splice(index, 1);
      }
    },
    showSymptomDialog(type) {
      this.dialogVisible = true;
      this.dialogMode = type;
      if (type === 'current') {
        this.dialogType = '现病史';
        this.dialogInitialSelected = this.currentIllnesses.map(s => s.id);
      } else if (type === 'past') {
        this.dialogType = '既往病史';
        this.dialogInitialSelected = this.pastIllnesses.map(s => s.id);
      }
    },
    confirmSymptoms(selectedSymptoms) {
      if (this.dialogMode === 'current') {
        this.currentIllnesses = selectedSymptoms;
      } else if (this.dialogMode === 'past') {
        this.pastIllnesses = selectedSymptoms;
      }
      this.dialogVisible = false;
    },
    cancelDialog() {
      this.dialogVisible = false;
    },
    async saveAssessment() {
      try {
        const now = new Date();
        const timeStr = `${now.getFullYear()}-${String(now.getMonth() + 1).padStart(2, '0')}-${String(now.getDate()).padStart(2, '0')} ${String(now.getHours()).padStart(2, '0')}:${String(now.getMinutes()).padStart(2, '0')}`;

        // 准备要保存的文件数据（只保留URL或文件路径）
        const processedFiles = {};
        Object.keys(this.uploadFiles).forEach(type => {
          if (this.uploadFiles[type] && this.uploadFiles[type].length) {
            processedFiles[type] = this.uploadFiles[type].map(file => typeof file === 'string' ? file : file.url || file.name);
          }
        });

        // 准备要保存的详情数据
        const details = {
          currentIllnesses: this.currentIllnesses,
          pastIllnesses: this.pastIllnesses,
          forbiddenMedicine: this.medicalForm.forbiddenMedicine,
          diet: this.diet,
          conclusion: this.medicalForm.conclusion,
          files: processedFiles
        };

        // 更新健康评估
        await this.$store.dispatch('updateHealthAssessment', {
          patientId: parseInt(this.id),
          status: this.medicalForm.admissionDecision === 'admit' ? 'completed' : 'rejected',
          time: timeStr,
          operator: '张医生',
          // 添加经办人信息
          details: details
        });

        // 如果当前评估和照护评估都已完成，标记入院流程完成
        if ((this.patient.healthAssessment.status === 'completed' || this.medicalForm.admissionDecision === 'admit') && this.patient.careAssessment.status === 'completed') {
          await this.$store.dispatch('finishAdmission', parseInt(this.id));
        }

        // 返回评估列表页面
        this.$router.push('/assessment');
      } catch (error) {
        console.error('保存评估失败:', error);
        // 这里可以添加错误提示
      }
    },
    cancelAssessment() {
      // 取消编辑，返回上一页
      this.$router.push('/assessment');
    },
    setCareLevel(category, item, level) {
      this.careForm[category][item] = level;
    },
    getProgressWidth(category, item) {
      const level = this.careForm[category][item];
      return `${(level + 1) * 25}%`;
    },
    async saveCareAssessment() {
      try {
        const now = new Date();
        const timeStr = `${now.getFullYear()}-${String(now.getMonth() + 1).padStart(2, '0')}-${String(now.getDate()).padStart(2, '0')} ${String(now.getHours()).padStart(2, '0')}:${String(now.getMinutes()).padStart(2, '0')}`;

        // 准备要保存的照护评估详情数据
        const details = {
          selfCare: {
            ...this.careForm.selfCare
          },
          cognition: {
            ...this.careForm.cognition
          },
          emotion: {
            ...this.careForm.emotion
          },
          extreme: {
            ...this.careForm.extreme
          }
        };

        // 更新照护评估
        await this.$store.dispatch('updateCareAssessment', {
          patientId: parseInt(this.id),
          status: 'completed',
          time: timeStr,
          operator: '李护士',
          // 添加经办人信息
          details: details
        });

        // 如果健康评估和照护评估都已完成，标记入院流程完成
        if (this.patient.healthAssessment.status === 'completed' && (this.patient.careAssessment.status === 'completed' || details)) {
          await this.$store.dispatch('finishAdmission', parseInt(this.id));
        }

        // 返回评估列表页面
        this.$router.push('/admission-assessment/list');
      } catch (error) {
        console.error('保存照护评估失败:', error);
        // 这里可以添加错误提示
      }
    },
    async handleMedicalReportUpload(file) {
      try {
        const formData = new FormData();
        formData.append('file', file);
        formData.append('patientId', this.id);
        await this.$store.dispatch('uploadMedicalReport', formData);
        this.$message.success('医疗报告上传成功');
      } catch (error) {
        console.error('上传医疗报告失败:', error);
        this.$message.error('上传医疗报告失败');
      }
    }
  },
  created() {
    this.loadAssessmentDetails();
    this.loadAllSymptoms();
  }
};
