import Vue from 'vue'
import VueRouter from 'vue-router'
import HomeView from '../views/HomeView.vue'
import Login from '../views/Login.vue'
import PatientAssessment from '../views/PatientAssessment.vue'
// eslint-disable-next-line no-unused-vars
import HealthAssessment from '../views/HealthAssessment.vue'

Vue.use(VueRouter)

const routes = [
  {
    path: '/',
    redirect: '/home'
  },
  {
    path: '/home',
    component: HomeView,
    children: [
      {
        path: '',
        redirect: '/assessment'
      },
      {
        path: '/assessment',
        name: 'PatientAssessment',
        component: PatientAssessment,
        meta: { title: '入院评估' }
      },
      {
        path: '/assessment/health/:id',
        name: 'HealthAssessment',
        component: HealthAssessment,
        props: true,
        meta: { title: '健康评估' }
      },
      {
        path: '/admission-details',
        name: 'AdmissionDetails',
        component: () => import('../views/AdmissionDetails.vue'),
        meta: { title: '入院评估详情' }
      },
      {
        path: '/admission-care',
        name: 'AdmissionCare',
        component: () => import('../views/AdmissionCare.vue'),
        meta: { title: '入院评估-照护' }
      },
      {
        path: '/discharge',
        name: 'Discharge',
        component: () => import('../views/Discharge.vue'),
        meta: { title: '离院办理' }
      },
      {
        path: '/discharge-elder',
        name: 'DischargeElder',
        component: () => import('../views/DischargeElder.vue'),
        meta: { title: '长者-出院办理' }
      }
    ]
  }
]

const router = new VueRouter({
  routes
})

// 设置页面标题
router.beforeEach((to, from, next) => {
  document.title = to.meta.title ? `${to.meta.title} - 护理管理系统` : '护理管理系统'
  next()
})

export default router
