import request from '@/utils/request'

/**
 * 获取健康评估详情
 * @param {number} assessmentId - 评估ID
 * @returns {Promise} - 返回评估详情数据
 */
export function getHealthAssessmentDetail(assessmentId) {
    return request({
        url: '/health-assessment/detail',
        method: 'get',
        params: {
            assessment_id: assessmentId
        }
    })
}

/**
 * 提交健康评估
 * @param {Object} data - 评估数据
 * @returns {Promise} - 返回提交结果
 */
export function submitHealthAssessment(data) {
    return request({
        url: '/health-assessment/submit',
        method: 'post',
        data
    })
}

/**
 * 创建健康评估
 * @param {Object} data - 评估数据
 * @returns {Promise} - 返回创建结果
 */
export function createHealthAssessment(data) {
    return request({
        url: '/health-assessment/create',
        method: 'post',
        data
    })
}

/**
 * 更新健康评估
 * @param {Object} data - 评估数据
 * @returns {Promise} - 返回更新结果
 */
export function updateHealthAssessment(data) {
    return request({
        url: '/health-assessment/update',
        method: 'post',
        data
    })
}

/**
 * 获取健康评估列表
 * @param {Object} params - 查询参数
 * @returns {Promise} - 返回评估列表数据
 */
export function getHealthAssessmentList(params) {
    return request({
        url: '/health-assessment/list',
        method: 'get',
        params
    })
}

/**
 * 上传医疗报告
 * @param {FormData} formData - 包含文件和评估ID的表单数据
 * @returns {Promise} - 返回上传结果
 */
export function uploadMedicalReport(formData) {
    return request({
        url: '/medical-report/upload',
        method: 'post',
        data: formData,
        headers: {
            'Content-Type': 'multipart/form-data'
        }
    })
}
